body {
            font-family: Arial, sans-serif;
               display: flex;
             justify-content: center;
             align-items: center;
             height: 100vh;
             margin: 0;
             background-color: #f0f0f0;
         }
         #container {
             text-align: center;
         }
         #element-to-capture {
             width: 300px;
             height: 200px;
             background: linear-gradient(90deg, red, orange, yellow, green, blue, indigo, violet);
             box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
             border-radius: 8px;
             margin-bottom: 20px;
         }
         button {
             padding: 10px 20px;
             font-size: 16px;
             color: white;
             background-color: #007bff;
             border: none;
             border-radius: 4px;
             cursor: pointer;
         }
         button:hover {
             background-color: #0056b3;
         }

